    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import flash from "connect-flash";
import passport from "passport";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import "./auth"; // Initialize passport strategies

// Load environment variables
import dotenv from 'dotenv';
dotenv.config();

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
if (!process.env.SESSION_SECRET) {
  throw new Error('SESSION_SECRET environment variable is required');
}

app.use(session({
  secret: process.env.SESSION_SECRET,
  resave: false,
  saveUninitialized: false,
  cookie: {
    httpOnly: true,
    sameSite: 'lax',
    secure: process.env.NODE_ENV === 'production',
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

if (process.env.NODE_ENV === 'production') {
  app.set('trust proxy', 1);
}

// Initialize Flash and Passport
app.use(flash());
app.use(passport.initialize());
app.use(passport.session());

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen(port, 'localhost', () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import passport from "passport";
import { storage } from "./storage";
import { fetchUpcomingEvents, deleteCalendarEvent, createCalendarEvent } from "./googleCalendar";
import { getMistralResponse, extractMeetingIntent, generateMeetingTitles, type MistralMessage } from "./mistral";
import { createEventRequestSchema } from "../shared/schema.js";

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.get('/api/auth/google', passport.authenticate('google', {
    scope: ['profile', 'email', 'https://www.googleapis.com/auth/calendar'],
    accessType: 'offline',
    prompt: 'consent'
  }));

  app.get('/api/auth/google/callback', 
    passport.authenticate('google', { 
      failureRedirect: '/',
      failureMessage: true 
    }),
    (req: Request, res: Response) => {
      console.log('OAuth callback successful, user:', req.user);
      // Successful authentication, redirect to dashboard
      res.redirect('/');
    }
  );

  // Add error handling for OAuth failures
  app.get('/api/auth/error', (req: Request, res: Response) => {
    console.log('OAuth error:', req.flash());
    res.status(401).json({ error: 'Authentication failed', details: req.flash() });
  });

  app.post('/api/auth/logout', (req: Request, res: Response) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ error: 'Logout failed' });
      }
      res.json({ success: true });
    });
  });

  app.get('/api/auth/user', (req: Request, res: Response) => {
    if (req.isAuthenticated()) {
      res.json({ user: req.user });
    } else {
      res.status(401).json({ error: 'Not authenticated' });
    }
  });

  // Calendar routes
  app.get('/api/calendar/events', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const user = req.user as any;
      const events = await fetchUpcomingEvents(user);
      res.json({ events });
    } catch (error: any) {
      console.error('Error fetching calendar events:', error);
      res.status(500).json({ error: error.message || 'Failed to fetch calendar events' });
    }
  });

  // Delete calendar event
  app.delete('/api/calendar/events/:eventId', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const user = req.user as any;
      const { eventId } = req.params;

      await deleteCalendarEvent(user, eventId);
      res.json({ success: true, message: 'Event deleted successfully' });
    } catch (error: any) {
      console.error('Error deleting calendar event:', error);
      res.status(500).json({ error: error.message || 'Failed to delete calendar event' });
    }
  });

  // Mistral AI chat endpoint
  app.post('/api/chat', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const { messages }: { messages: MistralMessage[] } = req.body;

      if (!messages || !Array.isArray(messages)) {
        return res.status(400).json({ error: 'Invalid messages format' });
      }

      const response = await getMistralResponse(messages);
      res.json({ response });
    } catch (error: any) {
      console.error('Error getting AI response:', error);
      res.status(500).json({ error: error.message || 'Failed to get AI response' });
    }
  });

  // AI meeting extraction endpoint
  app.post('/api/ai/extract-meeting', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const { message, context } = req.body;

      if (!message || typeof message !== 'string') {
        return res.status(400).json({ error: 'Message is required' });
      }

      const extraction = await extractMeetingIntent(message, context || '');
      res.json({ extraction });
    } catch (error: any) {
      console.error('Error extracting meeting intent:', error);
      res.status(500).json({ error: error.message || 'Failed to extract meeting intent' });
    }
  });

  // AI title generation endpoint
  app.post('/api/ai/generate-titles', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const { purpose, participants, context } = req.body;

      if (!purpose || typeof purpose !== 'string') {
        return res.status(400).json({ error: 'Purpose is required' });
      }

      const titleSuggestion = await generateMeetingTitles(
        purpose, 
        participants || [], 
        context || ''
      );
      res.json({ titleSuggestion });
    } catch (error: any) {
      console.error('Error generating meeting titles:', error);
      res.status(500).json({ error: error.message || 'Failed to generate meeting titles' });
    }
  });

  // Create calendar event with enhanced features
  app.post('/api/calendar/events', async (req: Request, res: Response) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ error: 'Not authenticated' });
    }

    try {
      const user = req.user as any;

      // Validate request body using Zod schema
      const validationResult = createEventRequestSchema.safeParse(req.body);
      if (!validationResult.success) {
        return res.status(400).json({ 
          error: 'Invalid request data', 
          details: validationResult.error.errors 
        });
      }

      const eventData = validationResult.data;

      // Create the calendar event with optional Meet link
      const createdEvent = await createCalendarEvent(
        user, 
        eventData, 
        eventData.createMeetLink
      );

      // Store event in our database if needed
      try {
        await storage.createEvent({
          googleEventId: createdEvent.googleEventId!,
          userId: user.id,
          title: eventData.title,
          description: eventData.description || '',
          startTime: new Date(eventData.startTime),
          endTime: new Date(eventData.endTime),
          meetingLink: createdEvent.meetingLink || '',
          attendees: eventData.attendees.map(a => a.email),
        });
      } catch (dbError) {
        // Log database error but don't fail the request
        console.error('Error storing event in database:', dbError);
      }

      res.json({ 
        success: true, 
        event: createdEvent,
        message: 'Meeting created successfully!' 
      });
    } catch (error: any) {
      console.error('Error creating calendar event:', error);
      res.status(500).json({ error: error.message || 'Failed to create calendar event' });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { users, type User, type InsertUser } from "../shared/schema";
import { eq } from "drizzle-orm";

// Ensure environment variables are loaded
if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL environment variable is required");
}

const sql = neon(process.env.DATABASE_URL);
const db = drizzle(sql);

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByGoogleId(googleId: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserTokens(googleId: string, accessToken: string, refreshToken?: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByGoogleId(googleId: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.googleId, googleId)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values({
      ...insertUser,
      picture: insertUser.picture || null,
      accessToken: insertUser.accessToken || null,
      refreshToken: insertUser.refreshToken || null
    }).returning();
    return result[0];
  }

  async updateUserTokens(googleId: string, accessToken: string, refreshToken?: string): Promise<void> {
    await db.update(users)
      .set({ 
        accessToken, 
        refreshToken: refreshToken || null 
      })
      .where(eq(users.googleId, googleId));
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, json, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  googleId: text("google_id").notNull().unique(),
  email: text("email").notNull().unique(),
  name: text("name").notNull(),
  picture: text("picture"),
  accessToken: text("access_token"),
  refreshToken: text("refresh_token"),
});

export const events = pgTable("events", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  googleEventId: text("google_event_id").unique(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  title: text("title").notNull(),
  description: text("description"),
  startTime: timestamp("start_time").notNull(),
  endTime: timestamp("end_time").notNull(),
  meetingLink: text("meeting_link"),
  attendees: json("attendees").$type<string[]>().default([]),
  agenda: text("agenda"),
  transcriptGenerated: boolean("transcript_generated").default(false),
});

export const tasks = pgTable("tasks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  eventId: varchar("event_id").references(() => events.id),
  title: text("title").notNull(),
  description: text("description"),
  assignee: text("assignee").notNull(),
  deadline: timestamp("deadline"),
  status: text("status").notNull().default("pending"), // pending, in_progress, completed
  createdAt: timestamp("created_at").defaultNow(),
});

export const chatMessages = pgTable("chat_messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  role: text("role").notNull(), // user, assistant
  content: text("content").notNull(),
  timestamp: timestamp("timestamp").defaultNow(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({ id: true });
export const insertEventSchema = createInsertSchema(events).omit({ id: true });
export const insertTaskSchema = createInsertSchema(tasks).omit({ id: true, createdAt: true });
export const insertChatMessageSchema = createInsertSchema(chatMessages).omit({ id: true, timestamp: true });

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Event = typeof events.$inferSelect;
export type InsertEvent = z.infer<typeof insertEventSchema>;
export type Task = typeof tasks.$inferSelect;
export type InsertTask = z.infer<typeof insertTaskSchema>;
export type ChatMessage = typeof chatMessages.$inferSelect;
export type InsertChatMessage = z.infer<typeof insertChatMessageSchema>;

// Enhanced chat message types for UI blocks
export const attendeeSchema = z.object({
  email: z.string().email(),
  name: z.string().optional(),
  verified: z.boolean().default(false),
});

export const meetingExtractionSchema = z.object({
  intent: z.enum(['create_meeting', 'schedule_meeting', 'other']),
  confidence: z.number().min(0).max(1),
  fields: z.object({
    startTime: z.string().optional(), // ISO 8601 string
    endTime: z.string().optional(),
    duration: z.number().optional(), // minutes
    purpose: z.string().optional(),
    participants: z.array(z.string()).default([]),
    suggestedTitle: z.string().optional(),
  }),
  missing: z.array(z.enum(['startTime', 'endTime', 'duration', 'participants'])).default([]),
});

export const titleSuggestionSchema = z.object({
  suggestions: z.array(z.string()).length(3),
  context: z.string(),
});

export const uiBlockSchema = z.discriminatedUnion('type', [
  z.object({
    type: z.literal('meeting_link_choice'),
    data: z.object({
      question: z.string(),
      meetingId: z.string(),
    }),
  }),
  z.object({
    type: z.literal('attendee_editor'),
    data: z.object({
      attendees: z.array(attendeeSchema),
      meetingId: z.string(),
    }),
  }),
  z.object({
    type: z.literal('title_suggestions'),
    data: z.object({
      suggestions: z.array(z.string()),
      currentTitle: z.string().optional(),
      meetingId: z.string(),
    }),
  }),
  z.object({
    type: z.literal('event_review'),
    data: z.object({
      title: z.string(),
      startTime: z.string(),
      endTime: z.string(),
      attendees: z.array(attendeeSchema),
      includeMeetLink: z.boolean(),
      meetingId: z.string(),
    }),
  }),
]);

export const enhancedChatMessageSchema = z.object({
  id: z.string(),
  role: z.enum(['user', 'assistant']),
  content: z.string(),
  timestamp: z.date(),
  uiBlock: uiBlockSchema.optional(),
  metadata: z.object({
    extraction: meetingExtractionSchema.optional(),
    meetingId: z.string().optional(),
  }).optional(),
});

// Types for enhanced features
export type Attendee = z.infer<typeof attendeeSchema>;
export type MeetingExtraction = z.infer<typeof meetingExtractionSchema>;
export type TitleSuggestion = z.infer<typeof titleSuggestionSchema>;
export type UIBlock = z.infer<typeof uiBlockSchema>;
export type EnhancedChatMessage = z.infer<typeof enhancedChatMessageSchema>;

// Event creation request schema
export const createEventRequestSchema = z.object({
  title: z.string(),
  startTime: z.string(), // ISO 8601
  endTime: z.string(), // ISO 8601
  description: z.string().optional(),
  attendees: z.array(attendeeSchema),
  createMeetLink: z.boolean().default(false),
});

export type CreateEventRequest = z.infer<typeof createEventRequestSchema>;

