import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Calendar, Shield, Zap, Bot } from "lucide-react";
import { useState } from "react";

interface GoogleAuthButtonProps {
  onSignIn: () => void;
  isLoading?: boolean;
}

export function GoogleAuthButton({ onSignIn, isLoading = false }: GoogleAuthButtonProps) {
  const [hoveredFeature, setHoveredFeature] = useState<string | null>(null);

  const features = [
    {
      id: "calendar",
      icon: Calendar,
      title: "Calendar Integration",
      description: "Access and manage your Google Calendar events seamlessly"
    },
    {
      id: "ai",
      icon: Bot,
      title: "AI Assistant", 
      description: "Intelligent scheduling and meeting management with conversation"
    },
    {
      id: "automation",
      icon: Zap,
      title: "Smart Automation",
      description: "Automated agenda generation, transcripts, and action item tracking"
    },
    {
      id: "secure",
      icon: Shield,
      title: "Secure & Private",
      description: "Your data stays protected with industry-standard OAuth security"
    }
  ];

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <div className="max-w-4xl w-full">
        <div className="text-center mb-12">
          <div className="inline-flex items-center gap-2 mb-4">
            <div className="p-3 bg-primary/10 rounded-xl">
              <Bot className="h-8 w-8 text-primary" />
            </div>
            <h1 className="text-4xl font-bold tracking-tight">AI Calendar Assistant</h1>
          </div>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Transform your meeting management with AI-powered scheduling, 
            automated workflows, and intelligent insights.
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8 items-start">
          <Card className="hover-elevate">
            <CardHeader className="text-center pb-6">
              <CardTitle className="text-2xl mb-2">Get Started</CardTitle>
              <p className="text-muted-foreground">
                Connect your Google account to unlock powerful calendar management
              </p>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="p-6 bg-muted/30 rounded-lg text-center">
                <svg viewBox="0 0 24 24" className="w-8 h-8 mx-auto mb-3">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
                <h3 className="font-semibold mb-2">Sign in with Google</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Secure OAuth authentication - we only access your calendar with your permission
                </p>
              </div>

              <Button 
                onClick={onSignIn}
                disabled={isLoading}
                size="lg"
                className="w-full"
                data-testid="button-google-signin"
              >
                {isLoading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2" />
                    Connecting...
                  </>
                ) : (
                  <>
                    <svg viewBox="0 0 24 24" className="w-4 h-4 mr-2">
                      <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                      <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                      <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                      <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                    </svg>
                    Continue with Google
                  </>
                )}
              </Button>

              <div className="flex items-center justify-center gap-2 text-xs text-muted-foreground">
                <Shield className="h-3 w-3" />
                Your privacy is protected
              </div>
            </CardContent>
          </Card>

          <div className="space-y-4">
            <h2 className="text-xl font-semibold mb-4">What you'll get:</h2>
            {features.map((feature) => {
              const Icon = feature.icon;
              return (
                <Card 
                  key={feature.id}
                  className={`cursor-pointer transition-all hover-elevate ${
                    hoveredFeature === feature.id ? 'ring-2 ring-primary/20' : ''
                  }`}
                  onMouseEnter={() => setHoveredFeature(feature.id)}
                  onMouseLeave={() => setHoveredFeature(null)}
                  data-testid={`feature-card-${feature.id}`}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start gap-3">
                      <div className="p-2 bg-primary/10 rounded-lg">
                        <Icon className="h-5 w-5 text-primary" />
                      </div>
                      <div className="flex-1">
                        <h3 className="font-medium mb-1">{feature.title}</h3>
                        <p className="text-sm text-muted-foreground">
                          {feature.description}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}

            <div className="pt-4">
              <div className="flex flex-wrap gap-2">
                <Badge variant="secondary" className="text-xs">Free to use</Badge>
                <Badge variant="secondary" className="text-xs">No credit card required</Badge>
                <Badge variant="secondary" className="text-xs">Instant setup</Badge>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/TaskBoard.tsx.content:
import { Clock, User, Calendar, CheckCircle, Circle, AlertCircle, Plus } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { format, isPast } from "date-fns";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface Task {
  id: string;
  title: string;
  description?: string;
  assignee: string;
  deadline?: Date;
  status: 'pending' | 'in_progress' | 'completed';
  eventTitle?: string;
}

interface TaskBoardProps {
  tasks: Task[];
  onUpdateTaskStatus: (taskId: string, status: Task['status']) => void;
  onTaskClick: (task: Task) => void;
  onAddTask?: (task: Omit<Task, 'id' | 'status'>) => void; // New prop for adding tasks
}

// Sample assignees - in a real app, this would come from your user management system
const sampleAssignees = [
  "Sarah Johnson",
  "Mike Chen",
  "Alex Rodriguez",
  "Emma Wilson",
  "David Kim"
];

const statusColumns = [
  { 
    status: 'pending' as const, 
    title: 'To Do', 
    color: 'text-muted-foreground',
    bgColor: 'bg-muted/20'
  },
  { 
    status: 'in_progress' as const, 
    title: 'In Progress', 
    color: 'text-blue-600 dark:text-blue-400',
    bgColor: 'bg-blue-50 dark:bg-blue-950/20'
  },
  { 
    status: 'completed' as const, 
    title: 'Completed', 
    color: 'text-green-600 dark:text-green-400',
    bgColor: 'bg-green-50 dark:bg-green-950/20'
  }
];

export function TaskBoard({ tasks, onUpdateTaskStatus, onTaskClick, onAddTask }: TaskBoardProps) {
  const [isAddTaskOpen, setIsAddTaskOpen] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    assignee: sampleAssignees[0],
    eventTitle: ''
  });

  const getTasksByStatus = (status: Task['status']) => {
    return tasks.filter(task => task.status === status);
  };

  const getStatusIcon = (status: Task['status']) => {
    switch (status) {
      case 'pending':
        return <Circle className="h-4 w-4" />;
      case 'in_progress':
        return <AlertCircle className="h-4 w-4" />;
      case 'completed':
        return <CheckCircle className="h-4 w-4" />;
    }
  };

  const isTaskOverdue = (task: Task) => {
    return task.deadline && task.status !== 'completed' && isPast(task.deadline);
  };

  const handleAddTask = () => {
    if (newTask.title.trim() && onAddTask) {
      onAddTask({
        title: newTask.title,
        description: newTask.description,
        assignee: newTask.assignee,
        eventTitle: newTask.eventTitle
      });
      // Reset form
      setNewTask({
        title: '',
        description: '',
        assignee: sampleAssignees[0],
        eventTitle: ''
      });
      setIsAddTaskOpen(false);
    }
  };

  return (
    <div className="h-full">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-full">
        {statusColumns.map((column) => {
          const columnTasks = getTasksByStatus(column.status);

          return (
            <Card key={column.status} className="h-fit lg:h-full">
              <CardHeader className="pb-3">
                <CardTitle className={`text-base flex items-center justify-between ${column.color}`}>
                  <div className="flex items-center gap-2">
                    {getStatusIcon(column.status)}
                    {column.title}
                  </div>
                  <div className="flex items-center gap-2">
                    {column.status === 'pending' && onAddTask && (
                      <Dialog open={isAddTaskOpen} onOpenChange={setIsAddTaskOpen}>
                        <DialogTrigger asChild>
                          <Button 
                            variant="ghost" 
                            size="icon"
                            className="h-6 w-6 p-0"
                            onClick={(e) => e.stopPropagation()}
                          >
                            <Plus className="h-4 w-4" />
                          </Button>
                        </DialogTrigger>
                        <DialogContent 
                          className="sm:max-w-[425px]"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <DialogHeader>
                            <DialogTitle>Add New Task</DialogTitle>
                            <DialogDescription>
                              Create a new task to track your work and assign it to a team member.
                            </DialogDescription>
                          </DialogHeader>
                          <div className="grid gap-4 py-4">
                            <div className="grid grid-cols-4 items-center gap-4">
                              <label htmlFor="title" className="text-right text-sm font-medium">
                                Title
                              </label>
                              <div className="col-span-3">
                                <Input
                                  id="title"
                                  value={newTask.title}
                                  onChange={(e) => setNewTask({...newTask, title: e.target.value})}
                                  placeholder="Task title"
                                  className="w-full"
                                />
                              </div>
                            </div>
                            <div className="grid grid-cols-4 items-center gap-4">
                              <label htmlFor="description" className="text-right text-sm font-medium">
                                Description
                              </label>
                              <div className="col-span-3">
                                <Textarea
                                  id="description"
                                  value={newTask.description}
                                  onChange={(e) => setNewTask({...newTask, description: e.target.value})}
                                  placeholder="Task description"
                                  className="w-full"
                                />
                              </div>
                            </div>
                            <div className="grid grid-cols-4 items-center gap-4">
                              <label htmlFor="event" className="text-right text-sm font-medium">
                                Event
                              </label>
                              <div className="col-span-3">
                                <Input
                                  id="event"
                                  value={newTask.eventTitle}
                                  onChange={(e) => setNewTask({...newTask, eventTitle: e.target.value})}
                                  placeholder="Related event (optional)"
                                  className="w-full"
                                />
                              </div>
                            </div>
                            <div className="grid grid-cols-4 items-center gap-4">
                              <label htmlFor="assignee" className="text-right text-sm font-medium">
                                Assignee
                              </label>
                              <div className="col-span-3">
                                <Select 
                                  value={newTask.assignee} 
                                  onValueChange={(value) => setNewTask({...newTask, assignee: value})}
                                >
                                  <SelectTrigger className="w-full">
                                    <SelectValue placeholder="Select assignee" />
                                  </SelectTrigger>
                                  <SelectContent>
                                    {sampleAssignees.map((assignee) => (
                                      <SelectItem key={assignee} value={assignee}>
                                        {assignee}
                                      </SelectItem>
                                    ))}
                                  </SelectContent>
                                </Select>
                              </div>
                            </div>
                          </div>
                          <div className="flex justify-end gap-2">
                            <Button
                              variant="outline"
                              onClick={() => setIsAddTaskOpen(false)}
                            >
                              Cancel
                            </Button>
                            <Button 
                              onClick={handleAddTask}
                              disabled={!newTask.title.trim()}
                            >
                              Add Task
                            </Button>
                          </div>
                        </DialogContent>
                      </Dialog>
                    )}
                    <Badge variant="secondary" className="text-xs">
                      {columnTasks.length}
                    </Badge>
                  </div>
                </CardTitle>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-3 max-h-[calc(100vh-300px)] overflow-y-auto">
                  {columnTasks.map((task) => (
                    <Card
                      key={task.id}
                      className={`cursor-pointer hover-elevate transition-colors ${column.bgColor}`}
                      onClick={() => onTaskClick(task)}
                      data-testid={`task-card-${task.id}`}
                    >
                      <CardContent className="p-4">
                        <div className="space-y-3">
                          <div>
                            <h4 className="font-medium text-sm line-clamp-2">
                              {task.title}
                            </h4>
                            {task.description && (
                              <p className="text-xs text-muted-foreground mt-1 line-clamp-2">
                                {task.description}
                              </p>
                            )}
                          </div>

                          {task.eventTitle && (
                            <div className="flex items-center gap-1.5">
                              <Calendar className="h-3 w-3 text-muted-foreground" />
                              <span className="text-xs text-muted-foreground truncate">
                                {task.eventTitle}
                              </span>
                            </div>
                          )}

                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-2">
                              <Avatar className="h-6 w-6">
                                <AvatarFallback className="text-xs">
                                  {task.assignee.split(' ').map(n => n[0]).join('').slice(0, 2)}
                                </AvatarFallback>
                              </Avatar>
                              <span className="text-xs text-muted-foreground">
                                {task.assignee}
                              </span>
                            </div>

                            {task.deadline && (
                              <div className={`flex items-center gap-1 ${isTaskOverdue(task) ? 'text-destructive' : 'text-muted-foreground'}`}>
                                <Clock className="h-3 w-3" />
                                <span className="text-xs">
                                  {format(task.deadline, 'MMM d')}
                                </span>
                              </div>
                            )}
                          </div>

                          <div className="flex gap-1">
                            {column.status !== 'completed' && (
                              <Button
                                size="sm"
                                variant="outline"
                                className="h-6 px-2 text-xs"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  const nextStatus = column.status === 'pending' ? 'in_progress' : 'completed';
                                  onUpdateTaskStatus(task.id, nextStatus);
                                }}
                                data-testid={`button-update-task-${task.id}`}
                              >
                                {column.status === 'pending' ? 'Start' : 'Complete'}
                              </Button>
                            )}
                            {column.status === 'completed' && (
                              <Button
                                size="sm"
                                variant="outline" 
                                className="h-6 px-2 text-xs"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  onUpdateTaskStatus(task.id, 'in_progress');
                                }}
                                data-testid={`button-reopen-task-${task.id}`}
                              >
                                Reopen
                              </Button>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}

                  {columnTasks.length === 0 && (
                    <div className="text-center py-8 text-muted-foreground">
                      <div className="opacity-50 mb-2">
                        {getStatusIcon(column.status)}
                      </div>
                      <p className="text-sm">No {column.title.toLowerCase()} tasks</p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

